context.queue = context.queue || [];

var totalAllowedQueueSize = 20;
var mqtt_connected = false;
var blocked = false;

// mqtt out node
var nextNode = RED.nodes.getNode("ae7f8962.dd8b18");
// status check
mqtt_connected = nextNode.brokerConn.connected;
if (context.queue.length >= totalAllowedQueueSize) blocked = true;

if (msg.payload.op == "reset"  ) {
   context.queue = [];
} else if ( msg.payload.op == "trigger" ) {
    // dequeue
    if ( context.queue.length === 0 ) msg = null; // no action
    else {
       if ( mqtt_connected ) msg.payload = context.queue.shift();
    }
} else {
    // enqueue
    if ( !blocked ) context.queue.push(msg.payload);
}

if (mqtt_connected) {
    if( !blocked )
        node.status({fill:"green",shape:"ring",text: '('+context.queue.length+') stored,connected' });
    else 
        node.status({fill:"red",shape:"ring",text: 'blocked,('+context.queue.length+') stored,connected' });
} else {
    if( !blocked ) 
        node.status({fill:"red",shape:"ring",text: '('+context.queue.length+') stored,disconnected' });
    else
        node.status({fill:"red",shape:"ring",text: 'blocked,('+context.queue.length+') stored,disconnected' });
}

return msg;

